{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"provenance":[{"file_id":"1Nd5me9KI3_klvMG6fvbNXWOLG9Ue3E4v","timestamp":1757238240241},{"file_id":"1r1faap6VWvwA2ZzkuO9unL-kA5vBi3MY","timestamp":1757236239698}],"authorship_tag":"ABX9TyOe674jXfpUGPPiZcY5B2vC"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","metadata":{"colab":{"base_uri":"https://localhost:8080/"},"id":"2993b320","outputId":"d9f8b4ae-ad12-48ff-afc3-6ae7dd51a6f1"},"source":["import sqlite3\n","\n","# Connect to SQLite Database\n","con = sqlite3.connect('library.db')\n","cur = con.cursor()\n","cur.execute('''\n","    CREATE TABLE IF NOT EXISTS books (\n","        bid TEXT PRIMARY KEY,\n","        title TEXT,\n","        author TEXT,\n","        status TEXT\n","    )\n","''')\n","cur.execute('''\n","    CREATE TABLE IF NOT EXISTS issued_books (\n","        bid TEXT PRIMARY KEY,\n","        issuedto TEXT\n","    )\n","''')\n","con.commit()\n","\n","def add_book():\n","    bid = input(\"Enter book ID: \")\n","    title = input(\"Enter book title: \")\n","    author = input(\"Enter author name: \")\n","    try:\n","        cur.execute('INSERT INTO books VALUES (?,?,?,?)', (bid, title, author, 'available'))\n","        con.commit()\n","        print(\"Success: Book added.\")\n","    except sqlite3.IntegrityError:\n","        print(f\"Error: Book with ID {bid} already exists.\")\n","\n","\n","def view_books():\n","    print(\"\\n--- All Books ---\")\n","    cur.execute('SELECT * FROM books')\n","    books = cur.fetchall()\n","    if books:\n","        for row in books:\n","            print(f\"ID: {row[0]}, Title: {row[1]}, Author: {row[2]}, Status: {row[3]}\")\n","    else:\n","        print(\"No books in the library.\")\n","    print(\"-----------------\")\n","\n","def issue_book():\n","    bid = input(\"Enter book ID to issue: \")\n","    user = input(\"Enter student ID: \")\n","    cur.execute('SELECT status FROM books WHERE bid=?', (bid,))\n","    result = cur.fetchone()\n","\n","    if result is None:\n","        print(f\"Error: Book with ID {bid} not found.\")\n","    elif result[0] == 'available':\n","        cur.execute('UPDATE books SET status=\"issued\" WHERE bid=?', (bid,))\n","        cur.execute('INSERT INTO issued_books VALUES (?, ?)', (bid, user))\n","        con.commit()\n","        print(\"Success: Book issued.\")\n","    else:\n","        print(\"Error: Book already issued.\")\n","\n","\n","def return_book():\n","    bid = input(\"Enter book ID to return: \")\n","    cur.execute('SELECT status FROM books WHERE bid=?', (bid,))\n","    result = cur.fetchone()\n","\n","    if result is None:\n","        print(f\"Error: Book with ID {bid} not found.\")\n","    elif result[0] == 'issued':\n","        cur.execute('UPDATE books SET status=\"available\" WHERE bid=?', (bid,))\n","        cur.execute('DELETE FROM issued_books WHERE bid=?', (bid,))\n","        con.commit()\n","        print(\"Success: Book returned.\")\n","    else:\n","        print(\"Error: Book was not issued.\")\n","\n","\n","def main_menu():\n","    while True:\n","        print(\"\\nLibrary Management System\")\n","        print(\"1. Add Book\")\n","        print(\"2. View All Books\")\n","        print(\"3. Issue Book\")\n","        print(\"4. Return Book\")\n","        print(\"5. Exit\")\n","\n","        choice = input(\"Enter your choice: \")\n","\n","        if choice == '1':\n","            add_book()\n","        elif choice == '2':\n","            view_books()\n","        elif choice == '3':\n","            issue_book()\n","        elif choice == '4':\n","            return_book()\n","        elif choice == '5':\n","            print(\"Exiting Library Management System.\")\n","            break\n","        else:\n","            print(\"Invalid choice. Please try again.\")\n","\n","# Run the main menu\n","main_menu()\n","\n","# Close the database connection when the program exits\n","con.close()"],"execution_count":null,"outputs":[{"output_type":"stream","name":"stdout","text":["\n","Library Management System\n","1. Add Book\n","2. View All Books\n","3. Issue Book\n","4. Return Book\n","5. Exit\n"]}]}]}